mapper.select:
select u.id driverId, u.user_name driverName, u.phone phone, u.id_card idCard, u.user_account driverAccount, d.province provinceName, d.city cityName, d.county countryName, u.version version from t_user u, t_driver_info d where u.is_delete = 0 and u.role = 2 and d.is_delete = 0 and u.id = d.driver_id
and u.id like concat('%', #{driverId}, '%')
and u.user_name like concat('%', #{driverName}, '%')
and d.province = #{provinceName}
and d.city = #{cityName}
and d.county = #{countryName}
order by u.create_time desc
